import { FaUser, FaMoneyBillWave, FaCreditCard, FaFileInvoiceDollar, FaExchangeAlt, FaPhone } from 'react-icons/fa';

function ButtonCard({ title, icon }) {
  let IconComponent;

  switch (icon) {
    case 'user':
      IconComponent = FaUser;
      break;
    case 'money':
      IconComponent = FaMoneyBillWave;
      break;
    case 'creditCard':
      IconComponent = FaCreditCard;
      break;
    case 'bills':
      IconComponent = FaFileInvoiceDollar;
      break;
    case 'transfers':
      IconComponent = FaExchangeAlt;
      break;
    case 'contact':
      IconComponent = FaPhone;
      break;
    default:
      IconComponent = FaUser;
  }

  return (
    <div className="bg-white shadow-lg rounded-md p-6 flex flex-col items-center justify-center">
      <IconComponent className="text-3xl mb-4" />
      <h3 className="text-xl font-semibold mb-2">{title}</h3>
      <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
        Explore
      </button>
    </div>
  );
}
export default ButtonCard;
